mapper.select:

	SELECT *
	FROM member
	WHERE member_id = #{member_id}
	AND member_pw = #{member_pw}

mapper.update:

	UPDATE member
	SET member_pw = #{member_pw},
		member_name = #{member_name},
	    member_age = ${member_age},
	    member_gender = #{member_gender}
	WHERE member_id = #{member_id}
